parameters.functionCode:
// Process hot leads for immediate notification
const hotLeads = $json.body.hot_leads || [];
const processingSummary = $json.body.processing_summary || {};
const timestamp = $json.body.timestamp;

if (hotLeads.length === 0) {
  return { skip: true, message: 'No hot leads to process' };
}

// Format hot leads for notifications
const formattedLeads = hotLeads.map(lead => {
  return {
    company: lead.company,
    intent_score: lead.intent_score,
    lead_id: lead.lead_id,
    vtiger_url: `https://rdsteamglobalpresenceorg.od2.vtiger.com/view/detail?module=Leads&id=${lead.lead_id.replace('2x', '')}`,
    priority: lead.intent_score >= 95 ? 'URGENT' : 'HIGH'
  };
});

// Create Slack message
const slackMessage = {
  text: `🔥 *${hotLeads.length} HOT LEAD${hotLeads.length > 1 ? 'S' : ''} DETECTED!*`,
  blocks: [
    {
      type: "header",
      text: {
        type: "plain_text",
        text: `🔥 ${hotLeads.length} Hot Lead${hotLeads.length > 1 ? 's' : ''} - Immediate Action Required`
      }
    },
    {
      type: "section",
      text: {
        type: "mrkdwn",
        text: `*Processing Summary:* ${processingSummary.created} leads created, ${hotLeads.length} with intent score ≥90`
      }
    }
  ]
};

// Add each hot lead as a section
formattedLeads.forEach(lead => {
  slackMessage.blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*${lead.company}*\n🎯 Intent Score: *${lead.intent_score}* (${lead.priority})\n📊 <${lead.vtiger_url}|View in VTiger>`
    },
    accessory: {
      type: "button",
      text: {
        type: "plain_text",
        text: "View Lead"
      },
      url: lead.vtiger_url,
      action_id: "view_lead"
    }
  });
});

slackMessage.blocks.push({
  type: "context",
  elements: [
    {
      type: "mrkdwn",
      text: `⏰ Processed: ${new Date(timestamp).toLocaleString()}`
    }
  ]
});

console.log('Hot leads notification prepared for:', hotLeads.length, 'leads');

return {
  hot_leads: formattedLeads,
  slack_message: slackMessage,
  notification_count: hotLeads.length,
  timestamp: timestamp
};

// Create task assignments for hot leads
const hotLeads = $json.hot_leads || [];
const tasks = [];

hotLeads.forEach(lead => {
  const urgency = lead.priority === 'URGENT' ? 'within 1 hour' : 'within 4 hours';

  tasks.push({
    title: `Follow up: ${lead.company} (Intent: ${lead.intent_score})`,
    description: `High intent lead - Contact ${urgency}\nVTiger: ${lead.vtiger_url}`,
    priority: lead.priority,
    due_date: lead.priority === 'URGENT' ? 
      new Date(Date.now() + 60*60*1000).toISOString() : // 1 hour
      new Date(Date.now() + 4*60*60*1000).toISOString(), // 4 hours
    lead_id: lead.lead_id,
    company: lead.company
  });
});

return {
  tasks: tasks,
  task_count: tasks.length,
  timestamp: new Date().toISOString()
};

parameters.responseBody:
{
  "success": true,
  "message": "No hot leads to process",
  "notifications_sent": 0,
  "timestamp": "{{ new Date().toISOString() }}"
}
